import React from 'react';

import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';


const StyledTableCell = withStyles((theme) => ({
    head: {
      backgroundColor: theme.palette.common.black,
      color: theme.palette.common.white,
    },
    body: {
      fontSize: 14,
    },
  }))(TableCell);

  const StyledTableRow = withStyles((theme) => ({
    root: {
      '&:nth-of-type(odd)': {
        backgroundColor: theme.palette.action.hover,
      },
    },
  }))(TableRow);

export default function StatBox({ stat }) {

    const statsMap = {
        team_name: 'Team:',
        points_per_game: 'Points Per Game:',
        assists_per_game: 'Assists:',
        blocks_per_game: 'Blocks:',
        three_point_percentage: 'Three Point Percentage:',
        field_goal_percentage: 'Field Goal Percentage:',
        rebounds_per_game: 'Rebounds:'
    }




    const label = statsMap[stat.label];
    const value = stat.label.includes('percentage') ? `${stat.value}%` : stat.value;

    return (
        <div>
            <TableContainer component={Paper}>
      <Table aria-label="customized table">
        <TableHead>
          <TableRow>
            <StyledTableCell>Dessert (100g serving)</StyledTableCell>

          </TableRow>
        </TableHead>
        <TableBody>

            <StyledTableRow>
              <StyledTableCell component="th" scope="row">

              </StyledTableCell>
              <StyledTableCell align="right">{label}</StyledTableCell>

            </StyledTableRow>

        </TableBody>
      </Table>
    </TableContainer>

            {label}
            {value}

        </div>
    )
}
