import React from 'react'
import eastTeams from '../eastTeams'
import westTeams from '../westTeams'
import { Row, Col, Card, ListGroup, Container } from 'react-bootstrap'
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));

const Teams = () => {

    const classes = useStyles();

    return (
        <div>
            <Container>
            <h2>East</h2>
            <Grid container spacing={3}>

            {eastTeams.map(east => (
        <Grid item xs={6} sm={3}>
          <Paper className={classes.paper}> {east.teamName}</Paper>
        </Grid>
        ))}  
      </Grid>
      </Container>


            <Container>
                <h2>East</h2>
                <Col>
                {eastTeams.map(east => (

                        <Card style={{ width: '10rem' }}>
                            <Card.Img style={{ width: '150px' }} variant="top" src={east.teamImage} />
                                <Card.Body>
                                <ListGroup.Item style={{ background: 'rgb(64, 80, 181)', color: 'white' }}> {east.teamName} </ListGroup.Item>
                                    <Card.Text>description</Card.Text>
                                </Card.Body>
                        </Card>   

                    ))}    
                </Col>
            </Container>


            <Container>
                <h1>West</h1>
                <Col>
                {westTeams.map(west => (
                    <Card style={{ width: '10rem' }}>
                        <Card.Img style={{ width: '150px' }} variant="top" src={west.teamImage} />
                            <Card.Body>
                            <ListGroup.Item style={{ background: 'rgb(64, 80, 181)', color: 'white' }}> {west.teamName} </ListGroup.Item>
                                <Card.Text>description</Card.Text>
                            </Card.Body>
                    </Card>   
                    ))}    
                </Col>
            </Container>
        </div>
    )
}

export default Teams
